<script>

    export default {
        name: 'jiexiantu',
        mounted() {
            this.hookupShow();
        },
        data() {
            return {
                huhaoList:[],
	            kehuSymbol:'image://data:image/svg+xml;base64,',
	            huhaoSymbol:'image://data:image/svg+xml;base64,'
            }
        },
        computed: {
	        chartOption:function () {
		        return{

                    tooltip: {
                        trigger: 'item',
                        triggerOn: 'mousemove'
                    },
                    series: [
                        {
                            type: 'tree',
                            name: 'jiexiantu',
                            data:  [{
                                name:this.cus_id,
	                            symbol:this.kehuSymbol,
	                            children:this.huhaoList
                            }],

                            left: '0',
                            right: '0',
                            top: '15%',
                            bottom: '20%',

                            symbol: 'rect',
                            symbolSize: 22,
                            orient: 'vertical',
                            initialTreeDepth: 1,
                            expandAndCollapse: true,

                            label: {
                                normal: {
                                    position: 'bottom',
                                    fontSize: 14,
                                }
                            },

                            leaves: {
                                label: {
                                    normal: {
                                        position: 'bottom',
                                        fontSize: 14,
                                    }
                                }
                            },

                            animationDurationUpdate: 750
                        }
                    ]
                }
            },
            cus_id: function () {
                return this.$store.getters.cus_id
            },
	        mychart:function () {
		        return this.$echarts.init(document.getElementById('mybox'))
            }
        },
        watch: {
            cus_id: function () {
                this.huhaoList=[];
                this.hookupShow();
            }
        },
        methods: {
            mouseMove: function () {
                event.preventDefault();
                if (this.dragParams.dragFlag) {
                    console.log('move');
                    this.dragParams.nowX = event.clientX;
                    this.dragParams.disX = this.dragParams.nowX - this.dragParams.currentX;
                    this.dragParams.to = Math.min(0, Math.max(parseInt(this.dragParams.currentLeft) + this.dragParams.disX, this.dragParams.min));
                    this.dragParams.drag.style.left = this.dragParams.to + 'px'
                }
            },
            mouseUp: function () {
                event.preventDefault();
                this.dragParams.min = this.$refs.mybox.parentNode.offsetWidth - this.$refs.mybox.offsetWidth;

                this.dragParams.dragFlag = false;
                this.dragParams.currentLeft = this.dragParams.drag.style.left;
                console.log('up', parseInt(this.dragParams.currentLeft));
                this.$refs.mybox.offsetParent.style.cursor = '';
            },
            mouseOut: function () {
                event.preventDefault();
                this.dragParams.dragFlag = false;
                this.$refs.mybox.offsetParent.cursor = '';
            },
            mouseDown: function () {
                event.preventDefault();
                this.dragParams.dragFlag = true;
                console.log('down', this.dragParams.currentLeft);
                this.dragParams.currentX = event.clientX;
                this.mouseMove();
                this.$refs.mybox.offsetParent.style.cursor = 'move';
            },
	        drawChart:function () {
		        this.mychart.clear();
		        this.mychart.setOption(this.chartOption);
		        mychart.on('click', function (params) {
                    console.log(params)
                })
            },
            hookupShow() {
                this.$http.post(this.$api.CLIENT_HOOKUP, {cus_id: this.cus_id}).then(res => {
                    console.log('接线图', res);
                    let data = res.data.data;
                    data.map(i=>{
                       this.huhaoList.push({
	                       name:i.user_no,
                           symbol:this.huhaoSymbol
                       })
                    });
                    this.drawChart();
                }, err => {
                    this.$api.errcallback(err);
                }).catch(err => {
                    this.$api.errcallback(err);
                })
            }
        },
    }
</script>
<template>
	<Card class="jiexiantu">

		<div id="mybox" style="position: absolute;top: 0;left: 0;right: 0px;bottom: 0"></div>

	</Card>
</template>
<style scoped>
	.jiexiantu {
		width: 100%;
		height: 202px;
		display: inline-block;
		vertical-align: top;
		position: relative;
		overflow: hidden;
	}

</style>